import React from "react";
import { Navig, useParams } from "react-router-dom";
import { Card, CardBody, CardTitle, CardText } from "reactstrap";

function FoodItem({ items, cantFind }) {
  const { id } = useParams();

  let item = items.find(items => items.id === id);
  if (!items) return <Navigate to={cantFind} />;


  return (
    <section>
      <Card>
        <CardBody>
          <CardTitle className="font-weight-bold text-center">
            {items.name}
          </CardTitle>
          <CardText className="font-italic">{items.description}</CardText>
          <p>
            <b>Recipe:</b> {items.recipe}
          </p>
          <p>
            <b>Serve:</b> {items.serve}
          </p>
        </CardBody>
      </Card>
    </section>
  );
}

export default FoodItem;
