import React from "react";
import { Link } from "react-router-dom";
import "./FoodMenu.css";
import {
  Card,
  CardBody,
  CardTitle,
  CardText,
  ListGroup,
  ListGroupItem
} from "reactstrap";

function Menu({ items, title, basePath }) {
  return (
    <section className="col-md-4 menu-column">
      <Card className="menu-card">
        <CardBody className="p-4">
          <CardTitle tag="h5" className="font-weight-bold text-center mb-3">
            {title} Menu
          </CardTitle>
          <CardText className="text-muted mb-4">
            Browse our delicious selection of {title.toLowerCase()}.
          </CardText>

          <ListGroup flush className="menu-list">
            {items.map(item => (
              <Link
                to={`/${basePath}/${item.id}`}
                key={item.id}
                className="text-decoration-none"
              >
                <ListGroupItem className="item-name py-2">
                  {item.name}
                </ListGroupItem>
              </Link>
            ))}
          </ListGroup>

          {/* slot for your form */}
          <div className="menu-form mt-4">
            <h6 className="mb-3">Add a new {title.slice(0, -1)}</h6>
            {/* put your <AddItemForm type={title.toLowerCase()} /> here */}
          </div>
        </CardBody>
      </Card>
    </section>
  );
}

export default Menu;
