import React, { useState, useEffect } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import "./App.css";
import Home from "./Home";
import SnackOrBoozeApi from "./Api";
import NavBar from "./NavBar";
import Menu from "./FoodMenu";
import Snack from "./FoodItem";

function App() {
  const [isLoading, setIsLoading] = useState(true);
  const [snacks, setSnacks]   = useState([]);
  const [drinks, setDrinks]   = useState([]);

  useEffect(() => {
    async function loadData() {
      const [snackData, drinkData] = await Promise.all([
        SnackOrBoozeApi.getSnacks(),
        SnackOrBoozeApi.getDrinks()
      ]);
      setSnacks(snackData);
      setDrinks(drinkData);
      setIsLoading(false);
    }
    loadData();
  }, []);

  if (isLoading) {
    return <p>Loading…</p>;
  }

  return (
    <div className="App">
      <BrowserRouter>
        <NavBar />
        <main>
          <Routes>
            <Route
              path="/"
              element={<Home snacks={snacks} drinks={drinks} />}
            />
            <Route
              path="/snacks"
              element={<Menu snacks={snacks} title="Snacks" />}
            />
            <Route
              path="/drinks"
              element={<Menu drinkss={snacks} title="Snacks" />}
            />
            <Route
              path="/snacks/:id"
              element={<Snack items={snacks} cantFind="/snacks" />}
            />
            <Route
              path="*"
              element={<p>Hmmm. I can’t seem to find what you want.</p>}
            />
          </Routes>
        </main>
      </BrowserRouter>
    </div>
  );
}

export default App;
